import * as React from 'react'
import styled, { css } from 'styled-components/macro'
import { Link } from 'react-router-dom'
import { IThumbnail } from '../data/IProject'
import { AccentColor, LoadingColor } from '../../GlobalStyles'
import { Tag } from '.'

interface IThumbnailProps {
	data: IThumbnail
}

export const Thumbnail: React.FC<IThumbnailProps> = (props: IThumbnailProps) => {
	const { data } = props
	const [isLoaded, setIsLoaded] = React.useState(false)
	const link = data.file ? data.file.source : `/page/${data.header.toLowerCase().replace(/ /g, '-')}`
	return (
		<ThumbnailContainer>
			<ThumbnailImage
				style={{
					backgroundImage: isLoaded ? `url('${data.thumbnail}')` : `url('data:image/png;base64,${transparentBase64}')`,
				}}
			/>
			<H3>{data.header}</H3>
			{/* We don't want to nest <a>'s*/}
			{data.file ? <ThumbnailLinkStandard href={link} target="_blank" rel="noopener noreferrer" /> : <ThumbnailLink to={link} />}

			{data.tags && (
				<Tags>
					[ {/* We want the thumbnail link behind the tag links */}
					{data.file ? (
						<ThumbnailLinkStandard href={link} target="_blank" rel="noopener noreferrer" />
					) : (
						<ThumbnailLink to={link} />
					)}
					{data.tags.map((tag, index) => (
						<Tag key={index} isLastTag={data.tags ? index === data.tags.length - 1 : false} tag={tag} />
					))}{' '}
					]
				</Tags>
			)}
			<LoadingImage src={data.thumbnail} onLoad={() => setIsLoaded(true)} />
		</ThumbnailContainer>
	)
}

const linkStyle = css`
	position: absolute;
	left: 0;
	top: 0;
	width: 100%;
	height: 100%;
	grid-column: gallery;
`

const ThumbnailLink = styled(Link)`
	${linkStyle}
`
const ThumbnailLinkStandard = styled.a`
	${linkStyle}
`
const ThumbnailImage = styled.div`
	width: 100%;
	height: 175px;
	background-size: cover;
	background-position: center;
	background-repeat: no-repeat;
	border: 3px solid #ffffff;
	background-color: ${LoadingColor};
	transition: background-image 500ms ease-in, border-color 100ms ease-in;
`

const H3 = styled.h3`
	width: 100%;
	text-align: center;
	padding-top: 8px;
	font-size: 1em;
	font-family: 'Museo_Slab_500_2';
	transition: color 100ms ease-in;
`
const Tags = styled.div`
	width: 100%;
	padding-top: 3px;
	text-align: center;
	font-size: 0.9em;
`

const ThumbnailContainer = styled.div`
	display: flex;
	flex-direction: column;

	&:hover {
		${ThumbnailImage} {
			border-color: ${AccentColor};
		}
		${H3} {
			color: ${AccentColor};
		}
	}
`
const LoadingImage = styled.img`
	display: none;
`

const transparentBase64 =
	''
